     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('valor','Valor','required|numeric');
		$this->form_validation->set_rules('quantidade','Quantidade','required|integer');
		$this->form_validation->set_rules('observacoes','Observacoes','max_length[255]');
