        }
    }  

    /*
     * Editing a itempedido
     */
    function edit($id_item)
    {   
        // check if the itempedido exists before trying to edit it
        $data['itempedido'] = $this->Itempedido_model->get_itempedido($id_item);
        
        if(isset($data['itempedido']['id_item']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('valor','Valor','required|numeric');
			$this->form_validation->set_rules('quantidade','Quantidade','required|integer');
			$this->form_validation->set_rules('observacoes','Observacoes','max_length[255]');
			$this->form_validation->set_rules('id_pedido','Id Pedido','required|integer');
			$this->form_validation->set_rules('id_produto','Id Produto','required|integer');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'id_produto' => $this->input->post('id_produto'),
					'valor' => $this->input->post('valor'),
					'quantidade' => $this->input->post('quantidade'),
					'observacoes' => $this->input->post('observacoes'),
					'id_pedido' => $this->input->post('id_pedido'),
                );

                $this->Itempedido_model->update_itempedido($id_item,$params);            
                redirect('itempedido/index');
            }
            else
