				$this->load->model('Produto_model');
				$data['all_produtos'] = $this->Produto_model->get_all_produtos();

                $data['_view'] = 'itempedido/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The itempedido you are trying to edit does not exist.');
    } 

    /*
     * Deleting itempedido
     */
    function remove($id_item)
    {
        $itempedido = $this->Itempedido_model->get_itempedido($id_item);

        // check if the itempedido exists before trying to delete it
        if(isset($itempedido['id_item']))
        {
            $this->Itempedido_model->delete_itempedido($id_item);
            redirect('itempedido/index');
        }
        else
            show_error('The itempedido you are trying to delete does not exist.');
    }
    
